(417, 80, 'programme_number', '1: Preventing Death from Infection'),
(418, 80, '_programme_number', 'field_5e25bc49e7912'),
(419, 80, 'hero_image', '81'),
(420, 80, '_hero_image', 'field_5e25baaada61b'),
(421, 80, 'hero_caption', 'Preventing death \r\nfrom severe infections'),
(422, 80, '_hero_caption', 'field_5e25bad0da61c'),
(423, 80, 'programme_lead', 'Professor Melita Gordon'),
(424, 80, '_programme_lead', 'field_5e25badfda61d'),
(425, 80, 'programme_lead_email', 'mgordon@mlw.mw'),
(426, 80, '_programme_lead_email', 'field_5e25bb3cda61e'),
(427, 80, 'intro', 'Malawi has one of the lowest life expectancies in the world (55yrs), with infectious diseases the leading cause of death in both adults and children. '),
(428, 80, '_intro', 'field_5e25bb5dda61f'),
(429, 80, 'deliverables', '3'),
(430, 80, '_deliverables', 'field_5e25bb976fdb1'),
(431, 80, 'deliverables_0_title', 'Key Deliverables Vaccine-related'),
(432, 80, '_deliverables_0_title', 'field_5e25bbed6fdb2'),
(433, 80, 'deliverables_0_text', '<ol class=\"ul1\">\r\n 	<li class=\"li1\">Sentinel hospital surveillance, community sampling and mathematical modelling to investigate sustained evidence of vaccine effectiveness, herd protection and changes to disease epidemiology for rotavirus and pneumococcus (Funded BMGF).</li>\r\n 	<li class=\"li1\"><strong>Childhood diarrhoea</strong>\r\n<ul class=\"ul1\">\r\n 	<li class=\"li1\">Novel rotavirus vaccine trial (RV3) to enhance early protection from severe rotavirus disease (Funded BMGF).</li>\r\n 	<li class=\"li1\">Characterise disease burden of norovirus and Shigella in severe diarrhoea to inform early phase vaccine trials (Funded Takeda).</li>\r\n</ul>\r\n</li>\r\n 	<li class=\"li1\"><strong>Respiratory infection</strong>\r\n<ul class=\"ul1\">\r\n 	<li class=\"li1\">Large scale pragmatic controlled trials nested within the infant immunisation schedule to evaluate alternate pneumococcal conjugate vaccine (PCV) schedules (2+1 and 1+1) and optimise herd protection. (French; BMGF invited proposal)</li>\r\n 	<li class=\"li1\">Factorial design trial of inactivated influenza vaccine and PCV in HIV infected pregnant women to prevent early infant respiratory morbidity and mortality (Bar-Zeev; seeking funding)</li>\r\n 	<li class=\"li1\">Establish pneumococcal CHIM -Experimental Human Pneumococcal Carriage model - for vaccine testing (novel vaccines targeting carriage) and pathogenesis studies using mucosal sampling. (Funded - MRC programme grant).</li>\r\n</ul>\r\n</li>\r\n 	<li class=\"li1\">Enteric infection\r\n<ul class=\"ul1\">\r\n 	<li class=\"li1\">Effectiveness studies with typhoid conjugate (Funded BMGF)</li>\r\n 	<li class=\"li1\">New phase 1/2 iNTS vaccines. (Gordon M; EDCTP application)</li>\r\n</ul>\r\n</li>\r\n</ol>'),
(434, 80, '_deliverables_0_text', 'field_5e25bbfb6fdb3'),
(435, 80, 'deliverables_1_title', 'Behaviour modification'),
(436, 80, '_deliverables_1_title', 'field_5e25bbed6fdb2'),
(437, 80, 'deliverables_1_text', '<p class=\"p1\">Roll-out and monitoring of improved health system referral pattern for severe childhood illness (funded) following successful pilot work. (Funded MRF and Scottish Government)</p>'),
(438, 80, '_deliverables_1_text', 'field_5e25bbfb6fdb3'),
(439, 80, 'deliverables_2_title', 'Syndrome management'),
(440, 80, '_deliverables_2_title', 'field_5e25bbed6fdb2'),
(441, 80, 'deliverables_2_text', '<ul class=\"ul1\">\r\n 	<li class=\"li1\">Randomised Controlled Trial (RCT) of antibiotics in severe paediatric gastroenteritis to reduce diarrhoea deaths in the post-rotavirus vaccine era (Funded WHO).</li>\r\n 	<li class=\"li1\">RCT of shortened treatment regimen in cryptococcal meningitis (Funded AMBITION - EDCTP and JGHT).</li>\r\n 	<li class=\"li1\">Improved understanding of sepsis, impact of DRI and the context in which AMR pathogens colonise humans in urban and rural Africa (Funded Wellcome Seed, WTCPP Fellow- Lewis).</li>\r\n 	<li class=\"li1\">Published AMR surveillance and mechanism data leading to successful trials of antibiotic stewardship (Funded BMGF, WTCPP Fellow - Lester).</li>\r\n 	<li class=\"li1\">RCT of ventilation to reduce death in cerebral malaria (Funded NIH)</li>\r\n</ul>'),
