(657, 91, 'deliverables_0_title', 'Theme 2 Key Deliverables HIV & TB'),
(658, 91, '_deliverables_0_title', 'field_5e25bbed6fdb2'),
(659, 91, 'deliverables_0_text', '<strong>Determine the impact of HIV self-testing (HIV-ST)</strong>\r\n<ul>\r\n 	<li style=\"list-style-type: none;\">\r\n<ul class=\"ul1\">\r\n 	<li class=\"li1\">Effect of HIV-ST on behaviour, transmission and undiagnosed HIV infection (Funded UNITAID)</li>\r\n 	<li class=\"li1\">Defining international policy &amp; practice for use of HIV-ST (Funded UNITAID,)</li>\r\n</ul>\r\n</li>\r\n</ul>\r\n<strong>Immunology</strong>\r\n<ul>\r\n 	<li style=\"list-style-type: none;\">\r\n<ul class=\"ul1\">\r\n 	<li class=\"li1\">Understanding pathogenic mechanisms and in particular the role of small macrophages in pulmonary virus persistence (Funded Wellcome)</li>\r\n 	<li class=\"li1\">Long-term effects of HIV and ART on host immune response to lower respiratory tract infections, particularly TB (Funded BMGF and NIH)</li>\r\n</ul>\r\n</li>\r\n</ul>\r\n<strong>Community intervention leading to measureable reduction in TB transmission</strong>\r\n<ul>\r\n 	<li style=\"list-style-type: none;\">\r\n<ul class=\"ul1\">\r\n 	<li class=\"li1\">Community education intervention (Funded Wellcome Senior Fellowship)</li>\r\n 	<li class=\"li1\">Diagnostic (digital radiography and laboratory) intervention (Macpherson; Wellcome CRCDF application)</li>\r\n</ul>\r\n</li>\r\n</ul>'),
(660, 91, '_deliverables_0_text', 'field_5e25bbfb6fdb3'),
(661, 91, 'deliverables_1_title', 'Malaria'),
(662, 91, '_deliverables_1_title', 'field_5e25bbed6fdb2'),
(663, 91, 'deliverables_1_text', '<strong>Determine viable regional approaches to malaria control</strong>\r\n<ul>\r\n 	<li style=\"text-align: left;\">Fine-scale methods to map and predict malaria heterogeneity (Funded Liberty Wildlife)targeted malaria intervention packages: transmission &amp; disease reduction (Terlouw; seeking funding)</li>\r\n 	<li style=\"list-style-type: none;\"></li>\r\n</ul>'),
(664, 91, '_deliverables_1_text', 'field_5e25bbfb6fdb3'),
(665, 91, 'deliverables_2_title', 'Transmission related vaccine studies'),
(666, 91, '_deliverables_2_title', 'field_5e25bbed6fdb2'),
(667, 91, 'deliverables_2_text', '<ul class=\"ul1\">\r\n 	<li class=\"li1\">Phylo-geographic and transmission reservoir descriptions of typhoid and iNTS (Funded BMGF)</li>\r\n 	<li class=\"li1\">National safety and efficacy RCT of malaria RTS,S vaccine (French, Terlouw; funding application EDCTP</li>\r\n</ul>'),
(668, 91, '_deliverables_2_text', 'field_5e25bbfb6fdb3'),
