(19187, 3058, 'caption', 'Welcome to MLW - \r\nWe conduct excellent research \r\nand train the next generation \r\nof researchers'),
(19188, 3058, '_caption', 'field_5e71df5428693'),
(19189, 3060, 'intro', 'Promote ethical research practice by facilitating two-way engagement between researchers and research participants through our public engagement component\r\nPromote a positive image of the research and training activities of MLW, raising the profile of research through our communications component.'),
(19190, 3060, '_intro', 'field_5e2819e827552'),
(19191, 3060, 'aim_0_image', '262'),
(19192, 3060, '_aim_0_image', 'field_5e281bd77f802'),
(19193, 3060, 'aim_0_intro', 'We aim to achieve our mission through imaginative, innovative initiatives that empower communities to make informed contributions in research and inspire the wider community to think about science. '),
(19194, 3060, '_aim_0_intro', 'field_5e281bec7f803'),
(19195, 3060, 'aim_0_text', '<p class=\"p1\">MLW is an exemplar scientific programme within the field of Public Engagement, benefitting from academic leadership in the Behavior and Health Group, as well as the full operational capacity of the Science Communication Department.</p>'),
(19196, 3060, '_aim_0_text', 'field_5e281c357f804'),
