(25404, 3551, 'intro', 'Promote ethical research practice by facilitating two-way engagement between researchers and research participants through our public engagement component\r\nPromote a positive image of the research and training activities of MLW, raising the profile of research through our communications component.'),
(25405, 3551, '_intro', 'field_5e2819e827552'),
(25406, 3551, 'aim_0_image', '262'),
(25407, 3551, '_aim_0_image', 'field_5e281bd77f802'),
(25408, 3551, 'aim_0_intro', 'We aim to achieve our mission through imaginative, innovative initiatives that empower communities to make informed contributions in research and inspire the wider community to think about science. '),
(25409, 3551, '_aim_0_intro', 'field_5e281bec7f803'),
(25410, 3551, 'aim_0_text', '<p class=\"p1\">MLW is an exemplar scientific programme within the field of Public Engagement, benefitting from academic leadership in the Behavior and Health Group, as well as the full operational capacity of the Science Communication Department.</p>'),
(25411, 3551, '_aim_0_text', 'field_5e281c357f804'),
