(26867, 3639, '_publications', 'field_5e25743d7e6a7'),
(26870, 3643, 'intro', 'Promote ethical research practice by facilitating two-way engagement between researchers and research participants through our public engagement component\r\nPromote a positive image of the research and training activities of MLW, raising the profile of research through our communications component.'),
(26871, 3643, '_intro', 'field_5e2819e827552'),
(26872, 3643, 'aim_0_image', '262'),
(26873, 3643, '_aim_0_image', 'field_5e281bd77f802'),
(26874, 3643, 'aim_0_intro', 'We aim to achieve our mission through imaginative, innovative initiatives that empower communities to make informed contributions in research and inspire the wider community to think about science. '),
(26875, 3643, '_aim_0_intro', 'field_5e281bec7f803'),
(26876, 3643, 'aim_0_text', '<p class=\"p1\">MLW is an exemplar scientific programme within the field of Public Engagement, benefitting from academic leadership in the Behavior and Health Group, as well as the full operational capacity of the Science Communication Department.</p>'),
(26877, 3643, '_aim_0_text', 'field_5e281c357f804'),
